function findLongestWordLength(str) {
  let word = str.split(' ');
  let longest = 0;
  for(let j = 0; j < word.length; j++){
    if(word[j].length > longest){
      longest = word[j].length;
    }
  } 
  return longest;
}

findLongestWordLength("The quick brown fox jumped over the lazy dog");
